/* Code tidied up by ScrapBook */
html { background: rgb(51, 51, 51) url("bg-texture-00.svg") repeat scroll 0% 0%; color: rgb(204, 204, 204); }
#layout-content { background: rgb(242, 242, 242) none repeat scroll 0% 0%; color: rgb(51, 51, 51); }
#layout-content:not(:only-child) { border-right: 0.25rem solid rgb(102, 102, 102); }
h1, h2, h3, h4, h5, h6 { font-weight: 500; color: rgb(51, 51, 51); }
header.title, h1::after, h2::after, h3::after { display: table; width: 100%; content: " "; margin-top: -1px; border-bottom: 1px dotted; }
a:link, a:visited { color: rgb(204, 204, 204); }
#layout-content a:link, #layout-content a:visited { color: rgb(51, 102, 153); }
a:hover, a:focus, #layout-content a:hover, #layout-content a:focus { color: rgb(174, 80, 141); border-color: rgb(174, 80, 141); outline: 0px none; }
ul { list-style-type: disc; }
h1.refname { color: rgb(121, 56, 98); }
.title a, .title { color: rgb(121, 56, 98); }
#flash-message .error, div.warning { background: rgb(244, 223, 223) none repeat scroll 0% 0%; border-color: rgb(234, 191, 191); }
.docs .classsynopsis, .refsect1 .fieldsynopsis, .refsect1 .dc-description, .docs .sect1 .dc-description, div.tip, blockquote.note, div.caution, div.warning { box-shadow: 0px 0px 0px 1px rgba(0, 0, 0, 0.15) inset; border-radius: 0px 0px 2px 2px; }
#flash-message .error a, div.warning a:link, div.warning a:visited, div.warning h2, div.warning h3 { color: rgb(153, 51, 102); }
#flash-message .success a, #flash-message a:hover, #flash-message a:focus, div.warning a:hover, div.warning a:focus { color: rgb(102, 153, 51); border-color: rgb(102, 153, 51); }
.navbar .nav > li > a:focus, .navbar .nav > li > a:hover { color: rgb(51, 51, 51); }
.navbar .nav > .active > a { box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.125) inset; }
.navbar .brand, .navbar .nav > li > a { color: rgb(226, 228, 239); border: 0px none; text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.25); }
.navbar .brand:hover, .navbar .nav > li > a:hover, .navbar .brand:focus, .navbar .nav > li > a:focus { color: rgb(255, 255, 255); }
.navbar .nav > li > a:focus, .navbar .nav > li > a:hover { background-color: transparent; color: rgb(255, 255, 255); }
.navbar .nav .active > a, .navbar .nav .active > a:hover, .navbar .nav .active > a:focus { color: rgb(255, 255, 255); background-color: rgb(79, 91, 147); }
.navbar .navbar-search .search-query { background-color: rgb(255, 255, 255); color: rgb(51, 51, 51); text-shadow: 0px 1px 0px rgb(255, 255, 255); border: 0px none; border-radius: 2px; box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2) inset; }
.navbar .navbar-search .search-query:focus { box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2) inset; }
.navbar .navbar-search .search-query:-moz-placeholder { color: rgb(153, 153, 153); }
.navbar { border-color: rgb(79, 91, 147); background: rgb(136, 146, 191) none repeat scroll 0% 0%; box-shadow: 0px 0.25em 0.25em rgba(0, 0, 0, 0.1); }
.navbar .brand { color: rgb(255, 255, 255); }
.navbar a { text-shadow: 0px 1px 0px rgb(255, 255, 255); }
#usernotes .count { background-color: rgb(121, 56, 98); color: rgb(255, 255, 255); border-radius: 4px; }
#usernotes .note .name { color: rgb(51, 51, 51); }
#usernotes .note .date { color: rgb(102, 102, 102); }
#usernotes .note .name:hover + .genanchor { color: black; }
#usernotes .note .text { transition: opacity 0.4s ease 0s; }
#usernotes .note .votes .tally { color: rgb(51, 51, 51); }
#usernotes .note .votes a { transition: border 0.4s ease 0s; }
#usernotes .note .text, .example-contents > [class$="code"], .example-contents.screen, .informalexample .literallayout { background-color: rgb(255, 255, 255); box-shadow: 0px 0px 0px 1px rgba(0, 0, 0, 0.15) inset; border-radius: 0px 0px 2px 2px; }
.refsect1 .example-contents > [class$="code"], .refsect1 .example-contents.screen { background-color: rgb(255, 255, 255); }
.phpcode span.html { color: black; background-color: transparent; }
div.phpcode span.default { color: rgb(51, 102, 153); background-color: transparent; }
div.phpcode span.keyword { color: rgb(102, 153, 51); background-color: transparent; }
div.phpcode span.string { color: rgb(204, 51, 51); background-color: transparent; }
.para var, .simpara var { background-color: rgb(230, 230, 230); border-radius: 2px; color: rgb(51, 51, 51); padding: 2px 4px; white-space: nowrap; font: 14px/1.46 "Source Code Pro",monospace; }
#layout-content a.genanchor:link, #layout-content a.genanchor:visited { color: transparent; border-bottom: medium none; }
#layout-content a.genanchor:hover, #layout-content a.genanchor:focus { color: rgb(51, 51, 51); border-bottom: medium none; }
#breadcrumbs { color: rgb(153, 153, 153); }
.layout-menu ul.child-menu-list li:first-child a { border-top-color: rgb(102, 102, 102); }
.layout-menu ul.child-menu-list li.current a { color: rgb(174, 80, 141); border-bottom-color: rgb(174, 80, 141); }
.layout-menu ul.child-menu-list a { border-color: rgb(102, 102, 102); }
.headsup { padding: 0.25rem 0px; height: 1.5rem; border-bottom: 0.125rem solid rgb(102, 153, 102); background-color: rgb(153, 204, 153); color: rgb(255, 255, 255); }
.headsup, .headsup a { margin: 0px auto; text-align: center; color: rgb(255, 255, 255); }
